$(window).load(function(){$('.flexslider').flexslider({animation:"fade"});var maxHeight=0,unitsLength=$('.category-unit').length;for(var i=0;i<unitsLength;i++){if($('.category-unit').eq(i).height()>maxHeight){maxHeight=$('.category-unit').eq(i).height()}};for(var j=0;j<unitsLength;j++){if($('.category-unit').eq(j).height()!==maxHeight){$('.category-unit').eq(j).css('height',maxHeight)}}})